mapper.select:
SELECT * FROM tipboard
WHERE subject LIKE CONCAT('%', #{search}, '%')
ORDER BY num DESC LIMIT #{pageSize} OFFSET #{startRow}

mapper.update:
UPDATE tipboard
SET readcount = readcount + 1
WHERE num = #{num}

mapper.select:
SELECT count(*)
FROM tipboard
WHERE num = #{num} AND passwd = #{passwd}

mapper.update:
UPDATE tipboard
SET subject = #{subject}, content = #{content}
WHERE num = #{num}

mapper.select:
SELECT *
FROM tipboardcomment
WHERE num = #{num}
ORDER BY re_num
LIMIT #{pageSize} OFFSET #{startRow}

mapper.update:
UPDATE tipboard
SET commcount = commcount + 1
WHERE num = #{num}

UPDATE tipboard
SET commcount = commcount - 1
WHERE num = #{num}

UPDATE tipboardcomment
SET content = #{content}
WHERE re_num = #{reNum}
